package com.hck.zhuanqian.adapter;import java.util.List;import android.content.Context;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.hck.zhuanqian.R;import com.hck.zhuanqian.bean.OrderBean;public class DHListAdpter extends BaseAdapter {	private Context context;	private List<OrderBean> orderBeans;	public DHListAdpter(Context context, List<OrderBean> beans) {		this.context = context;		this.orderBeans = beans;	}	@Override	public int getCount() {		return orderBeans.size();	}	@Override	public Object getItem(int position) {		return orderBeans.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {	    convertView=LayoutInflater.from(context).inflate(R.layout.listview_duihuan_item, null);        Holder holder=null;        holder=new Holder();       try {            holder.nameTextView=(TextView) convertView.findViewById(R.id.dh_text1);            holder.contentTextView=(TextView) convertView.findViewById(R.id.dh_text2);            holder.timeTextView=(TextView) convertView.findViewById(R.id.dh_text3);            holder.nameTextView.setText(orderBeans.get(position).getName());            String content=orderBeans.get(position).getContent();            String content2= content.substring(0, content.indexOf("h"));            byte [] contents= content2.trim().getBytes();            int post = contents.length;            contents[post-1]='*';            contents[post-2]='*';            contents[post-3]='*';            String string1= new String(contents);            String string2=content.substring(content.indexOf("h"), content.length());            StringBuffer stringBuffer =new StringBuffer();            stringBuffer.append(string1).append(string2);            holder.contentTextView.setText(stringBuffer.toString().replace("h", "").replace("null", "").trim());            holder.timeTextView.setText(orderBeans.get(position).getTime().substring(5, 16));        } catch (Exception e) {            Log.d("hck", "eeee: "+e.toString());        }                return convertView;	}	static class Holder {		TextView nameTextView, contentTextView, timeTextView;	}}