package com.hck.zhuanqian.bean;import java.security.Timestamp;import org.codehaus.jackson.annotate.JsonAutoDetect;import org.codehaus.jackson.annotate.JsonProperty;import org.codehaus.jackson.map.annotate.JsonSerialize;@JsonSerialize(include = JsonSerialize.Inclusion.NON_NULL)@JsonAutoDetect(getterVisibility = JsonAutoDetect.Visibility.NONE, setterVisibility = JsonAutoDetect.Visibility.NONE)public class MessageBean {		@JsonProperty("id")	private long id;	@JsonProperty("uid")	private long uid;	@JsonProperty("time")	private String time;	@JsonProperty("content")	private String content;	@JsonProperty("laizi")	private String laizi;	public String getLaizi() {        return laizi;    }    public void setLaizi(String laizi) {        this.laizi = laizi;    }    public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public long getUid() {		return uid;	}	public void setUid(long uid) {		this.uid = uid;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}}