package com.hck.zhuanqian.bean;import org.codehaus.jackson.annotate.JsonAutoDetect;import org.codehaus.jackson.annotate.JsonProperty;import org.codehaus.jackson.map.annotate.JsonSerialize;@JsonSerialize(include = JsonSerialize.Inclusion.NON_NULL)@JsonAutoDetect(getterVisibility = JsonAutoDetect.Visibility.NONE, setterVisibility = JsonAutoDetect.Visibility.NONE)public class TuiGBean {	@JsonProperty("content")	private String content;	@JsonProperty("time")	private String time;	@JsonProperty("userName")	private String userName;	@JsonProperty("id")	private long id;	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getUserName() {		return userName;	}	public void setUserName(String userName) {		this.userName = userName;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}}