package com.hck.zhuanqian.ui;import java.util.ArrayList;import android.content.Context;import android.os.Bundle;import android.util.Log;import android.view.View;import com.hck.zhuanqian.R;import com.hck.zhuanqian.data.Contans;import com.qidian.intwal.QDOpenWall;import com.qidian.intwal.QDScoreCallBack;import com.qidian.intwal.ScoreAdMsg;import com.qidian.intwal.Utils;/** *  * @author *  */public class QiDianActivity extends BaseActivity {	private int point;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		initAd();		setContentView(R.layout.activity_play_dl);	}	private void initAd() {		try {			QDOpenWall.setAppId(this, Contans.QI_DIAN_KEY);		} catch (Exception e) {			showErrorToast();		}	}	public void startGetMoney(View view) {		QDOpenWall.initInterfaceType(this, new QDScoreCallBack() {			@Override			public void onSuccess(Context arg0, String arg1) {				point = 0;				try {					ArrayList<ScoreAdMsg> msgs = Utils.getScoreMsgList(arg1);					if (msgs == null) {						return;					}					for (int i = 0; i < msgs.size(); i++) {						ScoreAdMsg msg = msgs.get(i);						point = Integer.parseInt(msg.getScore());					}					if (point > 0) {						savePoint(Contans.AD_NMAE_QI_DIAN,point);					}				} catch (Exception e) {					Log.d("hck", "eeee: " + e.toString());					showGetMoneyErrorToast();				}			}		});		QDOpenWall.getInstance().show();	}}